selector_to_html = {"a[href=\"#term-python\"]": "<dt id=\"term-python\">python</dt><dd><p><a class=\"reference external\" href=\"https://www.python.org\">A snake</a></p><p>I recommend not playing with this until you have one of the condas.</p></dd>", "a[href=\"#jargon\"]": "<h1 class=\"tippy-header\" style=\"margin-top: 0;\">Jargon<a class=\"headerlink\" href=\"#jargon\" title=\"Permalink to this heading\">#</a></h1>", "a[href=\"#term-miniforge\"]": "<dt id=\"term-miniforge\">miniforge</dt><dd><p><a class=\"reference external\" href=\"https://github.com/conda-forge/miniforge\">A smol but slightly faster snake</a></p><p>Small like <a class=\"reference internal\" href=\"#term-miniconda\"><span class=\"xref std std-term\">miniconda</span></a>, but the community (conda-forge) driven minimalistic conda installer.</p><p><strong>Most of us in the lab use this.</strong></p></dd>", "a[href=\"conda/installconda.html\"]": "<h1 class=\"tippy-header\" style=\"margin-top: 0;\">Installing Miniforge<a class=\"headerlink\" href=\"#installing-miniforge\" title=\"Permalink to this heading\">#</a></h1><p>I use miniforge, so this tutorial will guide you through installing <a class=\"reference external\" href=\"https://github.com/conda-forge/miniforge\">Miniforge</a>.</p><p>In the repository:</p>", "a[href=\"#term-conda\"]": "<dt id=\"term-conda\">conda</dt><dd><p>[A fat snake]<a class=\"reference external\" href=\"https://docs.conda.io/en/latest/()\">https://docs.conda.io/en/latest/()</a></p><p>Python package management system</p><p>Install Guide: <a class=\"reference internal\" href=\"conda/installconda.html\"><span class=\"doc std std-doc\">Installing Miniforge</span></a></p></dd>", "a[href=\"#term-linux\"]": "<dt id=\"term-linux\">linux</dt><dd><p>Like Windows and MacOS, Linux is just another operating system, but unlike the others, it is open-source.</p></dd>", "a[href=\"#term-GitHub\"]": "<dt id=\"term-GitHub\">GitHub </dt><dd><p>An adult verson of <a class=\"reference internal\" href=\"#term-git\"><span class=\"xref std std-term\">git</span></a></p><p>\u2026 it\u2019s a web-based hosting service for git repositories (a place where you store data, files, resources).</p></dd>", "a[href=\"#term-conda-environments\"]": "<dt id=\"term-conda-environments\">conda environments</dt><dd><p>More information: <a class=\"reference internal\" href=\"conda/condaenv.html\"><span class=\"doc std std-doc\">Conda Environments</span></a></p></dd>", "a[href=\"#term-mambaforge\"]": "<dt id=\"term-mambaforge\">mambaforge</dt><dd><p><a class=\"reference external\" href=\"https://mamba.readthedocs.io/en/latest/installation.html\">A fast snake</a></p><p>Very similar to <a class=\"reference internal\" href=\"#term-miniconda\"><span class=\"xref std std-term\">miniconda</span></a>, but I hear it\u2019s faster</p></dd>", "a[href=\"#term-Slurm\"]": "<dt id=\"term-Slurm\">Slurm</dt><dd><p>Job management system on some supercompters.</p></dd>", "a[href=\"#term-unix\"]": "<dt id=\"term-unix\">unix</dt><dd><p>Like Linux, composed of kernals, shell, etc. <strong>but</strong> this is not open-source, you need a license for use.</p></dd>", "a[href=\"git/git.html\"]": "<h1 class=\"tippy-header\" style=\"margin-top: 0;\">git<a class=\"headerlink\" href=\"#git\" title=\"Permalink to this heading\">#</a></h1><p><code class=\"docutils literal notranslate\"><span class=\"pre\">git</span></code> is a version control system.\n<code class=\"docutils literal notranslate\"><span class=\"pre\">git</span></code> helps you keep track of code changes.\n<code class=\"docutils literal notranslate\"><span class=\"pre\">git</span></code> is used to collaborate on code.</p><p><a class=\"reference external\" href=\"https://github.com\">GitHub</a> is a cloud-based hosting service that lets you manage <code class=\"docutils literal notranslate\"><span class=\"pre\">git</span></code> repositories. If you have open-source projects that use <code class=\"docutils literal notranslate\"><span class=\"pre\">git</span></code>, then GitHub is designed to help you better manage them.</p>", "a[href=\"#term-anaconda\"]": "<dt id=\"term-anaconda\">anaconda</dt><dd><p><a class=\"reference external\" href=\"https://www.anaconda.com\">A snake that comes with conda</a></p><p>Uses <a class=\"reference internal\" href=\"#term-conda\"><span class=\"xref std std-term\">conda</span></a> and ships as a GUI including a ton of packages.</p></dd>", "a[href=\"#term-miniconda\"]": "<dt id=\"term-miniconda\">miniconda</dt><dd><p><a class=\"reference external\" href=\"https://docs.conda.io/projects/miniconda/en/latest/\">A smol snake</a></p><p>Like <a class=\"reference internal\" href=\"#term-anaconda\"><span class=\"xref std std-term\">anaconda</span></a>, but does not come with as many packages or a GUI.</p></dd>", "a[href=\"conda/condaenv.html\"]": "<h1 class=\"tippy-header\" style=\"margin-top: 0;\">Conda Environments<a class=\"headerlink\" href=\"#conda-environments\" title=\"Permalink to this heading\">#</a></h1><h2>Creating New Environments<a class=\"headerlink\" href=\"#creating-new-environments\" title=\"Permalink to this heading\">#</a></h2><p>A new conda environment can be created with <code class=\"docutils literal notranslate\"><span class=\"pre\">create</span></code>, and you can specifiy the version of python!</p>", "a[href=\"#term-git\"]": "<dt id=\"term-git\">git</dt><dd><p>Free and open source version control system designed to manage coding projects.</p><p>More information: <a class=\"reference internal\" href=\"git/git.html\"><span class=\"doc std std-doc\">git</span></a></p></dd>", "a[href=\"#term-Graphic-User-Interface\"]": "<dt id=\"term-Graphic-User-Interface\">Graphic User Interface</dt><dd><p>Programs/softwares that requires the user to interact with icons, buttons, and menus</p></dd>", "a[href=\"#term-bash\"]": "<dt id=\"term-bash\">bash</dt><dd><p>Bourne Again SHel, is a \u201ccommand\u201d language forthe the CLI/Terminal. I write alot of my scripts with in bash!</p></dd>"}
skip_classes = ["headerlink", "sd-stretched-link"]

window.onload = function () {
    for (const [select, tip_html] of Object.entries(selector_to_html)) {
        const links = document.querySelectorAll(` ${select}`);
        for (const link of links) {
            if (skip_classes.some(c => link.classList.contains(c))) {
                continue;
            }

            tippy(link, {
                content: tip_html,
                allowHTML: true,
                arrow: true,
                placement: 'auto-start', maxWidth: 500, interactive: false,

            });
        };
    };
    console.log("tippy tips loaded!");
};
